import { Component, OnInit } from '@angular/core';
import {
  FormBuilder,
  FormControl,
  FormGroup,
  Validators,
} from '@angular/forms';
import { ActivatedRoute, Router } from '@angular/router';
import { CategoryDTO } from 'src/app/Models/category.dto';
import { CategoryService } from 'src/app/Services/category.service';
import { LocalStorageService } from 'src/app/Services/local-storage.service';
import { SharedService } from 'src/app/Services/shared.service';

@Component({
  selector: 'app-category-form',
  templateUrl: './category-form.component.html',
  styleUrls: ['./category-form.component.scss'],
})
export class CategoryFormComponent implements OnInit {
  category: CategoryDTO;
  title: FormControl;
  description: FormControl;
  css_color: FormControl;

  categoryForm: FormGroup;
  isValidForm: boolean | null;

  private isUpdateMode: boolean;
  private validRequest: boolean;
  private categoryId: string | null;

  constructor(
    private activatedRoute: ActivatedRoute,
    private categoryService: CategoryService,
    private formBuilder: FormBuilder,
    private router: Router,
    private sharedService: SharedService,
    private localStorageService: LocalStorageService
  ) {
    this.isValidForm = null;
    this.categoryId = this.activatedRoute.snapshot.paramMap.get('id');
    this.category = new CategoryDTO('', '', '');
    this.isUpdateMode = false;
    this.validRequest = false;

    this.title = new FormControl(this.category.title, [
      Validators.required,
      Validators.maxLength(55),
    ]);

    this.description = new FormControl(this.category.description, [
      Validators.required,
      Validators.maxLength(255),
    ]);

    this.css_color = new FormControl(this.category.css_color, [
      Validators.required,
      Validators.maxLength(7),
    ]);

    this.categoryForm = this.formBuilder.group({
      title: this.title,
      description: this.description,
      css_color: this.css_color,
    });
  }

  ngOnInit() {
    let errorResponse: any;

    // update
    if (this.categoryId) {
      this.isUpdateMode = true;
      /* try {
        this.category = await this.categoryService.getCategoryById(
          this.categoryId
        );

        this.title.setValue(this.category.title);

        this.description.setValue(this.category.description);

        this.css_color.setValue(this.category.css_color);

        this.categoryForm = this.formBuilder.group({
          title: this.title,
          description: this.description,
          css_color: this.css_color,
        });
      } catch (error: any) {
        errorResponse = error.error;
        this.sharedService.errorLog(errorResponse);
      } */

      this.categoryService
        .getCategoryById(this.categoryId)
        .subscribe((categoryResult) => {
          this.category = categoryResult;
          this.title.setValue(this.category.title);
          this.description.setValue(this.category.description);
          this.css_color.setValue(this.category.css_color);
          this.categoryForm = this.formBuilder.group(
            {
              title: this.title,
              description: this.description,
              css_color: this.css_color,
            },
            (error: any) => {
              errorResponse = error.error;
              this.sharedService.errorLog(errorResponse);
            }
          );
        });
    }
  }

  private editCategory() {
    let errorResponse: any;
    let responseOK: boolean = false;
    if (this.categoryId) {
      const userId = this.localStorageService.get('user_id');
      if (userId) {
        this.category.userId = userId;
        /* try {
          await this.categoryService.updateCategory(
            this.categoryId,
            this.category
          );
          responseOK = true;
        } catch (error: any) {
          errorResponse = error.error;
          this.sharedService.errorLog(errorResponse);
        }

        await this.sharedService.managementToast(
          'categoryFeedback',
          responseOK,
          errorResponse
        );

        if (responseOK) {
          // Reset the form
          //this.registerForm.reset();
          // After reset form we set birthDate to today again (is an example)
          //this.birth_date.setValue(formatDate(new Date(), 'yyyy-MM-dd', 'en'));
          this.router.navigateByUrl('categories');
        } */
        this.categoryService
          .updateCategory(this.categoryId, this.category)
          .subscribe(
            (categoryResult) => {
              responseOK = true;
            },
            (error: any) => {
              errorResponse = error.error;
              this.sharedService.errorLog(errorResponse);
            },
            () => {
              this.sharedService.managementToast(
                'categoryFeedback',
                responseOK,
                errorResponse
              );
              if (responseOK) {
                this.router.navigateByUrl('categories');
              }
            }
          );
      }
    }
    return responseOK;
  }

  private async createCategory(): Promise<boolean> {
    let errorResponse: any;
    let responseOK: boolean = false;
    const userId = this.localStorageService.get('user_id');
    if (userId) {
      this.category.userId = userId;
      /* try {
        await this.categoryService.createCategory(this.category);
        responseOK = true;
      } catch (error: any) {
        errorResponse = error.error;
        this.sharedService.errorLog(errorResponse);
      } */

      this.categoryService.createCategory(this.category).subscribe(
        (categoryResult) => {
          responseOK = true;
        },
        (error: any) => {
          errorResponse = error.error;
          this.sharedService.errorLog(errorResponse);
        },
        () => {
          this.sharedService.managementToast(
            'categoryFeedback',
            responseOK,
            errorResponse
          );
          if (responseOK) {
            this.router.navigateByUrl('categories');
          }
        }
      );

      /* await this.sharedService.managementToast(
        'categoryFeedback',
        responseOK,
        errorResponse
      );

      if (responseOK) {
        // Reset the form
        //this.registerForm.reset();
        // After reset form we set birthDate to today again (is an example)
        //this.birth_date.setValue(formatDate(new Date(), 'yyyy-MM-dd', 'en'));
        this.router.navigateByUrl('categories');
      } */
    }

    return responseOK;
  }

  async saveCategory() {
    this.isValidForm = false;

    if (this.categoryForm.invalid) {
      return;
    }

    this.isValidForm = true;
    this.category = this.categoryForm.value;

    if (this.isUpdateMode) {
      this.validRequest = await this.editCategory();
    } else {
      this.validRequest = await this.createCategory();
    }
  }
}
