<div>
  <h1>Register</h1>
  <form [formGroup]="registerForm" (ngSubmit)="register()">
    <div>
      <label for="name">Name: </label>
      <input type="text" [formControl]="name" />
      <div
        *ngIf="name.errors && isValidForm != null && !isValidForm"
        [ngClass]="'error'"
      >
        <div *ngIf="name.errors.required">Name is required</div>
        <div *ngIf="name.errors.minlength">
          Name must be at least 5 characters long.
        </div>
        <div *ngIf="name.errors.maxlength">
          Name can be max 25 characters long.
        </div>
      </div>
    </div>
    <div>
      <label for="surname1">Surname1: </label>
      <input type="text" [formControl]="surname_1" />
      <div
        *ngIf="surname_1.errors && isValidForm != null && !isValidForm"
        [ngClass]="'error'"
      >
        <div *ngIf="surname_1.errors.required">Surname1 is required</div>
        <div *ngIf="surname_1.errors.minlength">
          Surname1 must be at least 5 characters long.
        </div>
        <div *ngIf="surname_1.errors.maxlength">
          Surname1 can be max 25 characters long.
        </div>
      </div>
    </div>
    <div>
      <label for="surname2">Surname2: </label>
